function showAndDisplayCity(pId){
	if($(pId)) {
		if($(pId).style.display == "block") {
			$(pId).style.display = "none";
		} else {
			if(pId == "indexSearchCity" || pId == "indexSearchCity2") {
				(pId == "indexSearchCity")?($(pId).innerHTML= indexSearchCity):($(pId).innerHTML= indexSearchCity2);
			}else{
				$(pId).innerHTML= indexMoreCity;
			}
			$(pId).style.display = "block";
		}
	}
}

/*首页html显示城市*/
function showAndDisplayCityHtml(pId){
	if($(pId)) {
		if($(pId).style.display == "block") {
			$(pId).style.display = "none";
		} else {
			if(pId == "indexSearchCity" ) {
				$(pId).innerHTML= indexSearchCityHtml;
			}else{
				$(pId).innerHTML= indexMoreCityHtml;
			}
			$(pId).style.display = "block";
		}
	}
}

/*首页html选中城市*/
function saveNowCityHtml(city){
	var v=city.replace(/^\s+|\s+$/ig,"");     //去掉city首尾的空白字符
	cookieSet("SEARCH_CITYKEY_COOKIE", encodeURIComponent(v), 1000*60*60*24*30, ".jobui.com");
	var re = new RegExp("(?:.+\\/)?(.+?)\\.(.+?)\\.(.+?)/","ig");
	var arr = re.exec(top.onloadUrl);
	s = RegExp.$1;
	document.location.href= "http://" + s + ".jobui.com/index_" + v + "/";
}

/*选中城市*/
function saveNowCity(city, pId, reloadWindow, url){
	var v=city.replace(/^\s+|\s+$/ig,"");     //去掉city首尾的空白字符
	if($("cityKw")) {
		$("cityKw").value = v;
	}
	if(!url) { url = location.href; reloadWindow = false; }        //若url不存在，则回到本页面
	if(url.indexOf("search.")!=-1) {
		city = encodeURIComponent(city);
		if($("jobKw")) {		
			var jobKw = $("jobKw").value;		//取得用户输入
			jobKw = encodeURIComponent(jobKw);
			url = url.replace(/jobKw=(.*?)&/ig, "jobKw="+jobKw+"&");
		}
		url = url.replace(/cityKw=(.*?)&/ig, "cityKw="+city+"&");
		reloadWindow = true;
	}
	if(pId) {
		showAndDisplayCity(pId);
	}
	if(reloadWindow) {
		cookieSet("SEARCH_CITYKEY_COOKIE", encodeURIComponent(v), 1000*60*60*24*30, ".jobui.com");
		if($("jobuiNowCity")) {
			$("jobuiNowCity").innerHTML=v;
		}
		if($("jobuiNowCity1")) {
			$("jobuiNowCity1").innerHTML=v;
		}
		if($("jobuiNowCity2")) {
			$("jobuiNowCity2").innerHTML=v;
		}
		if($("jobuiNowCity3")) {
			$("jobuiNowCity3").innerHTML=v;
		}
		if($("jobuiNowCity4")) {
			$("jobuiNowCity4").innerHTML=v;
		}
		if($("nowArea")) {//分类搜索
			$("nowArea").innerHTML=v;
		}
		if(url == location.href) {
			document.location.reload(true);
		} else {
			document.location.href=url;
		}
	} else {
		cookieSet("SEARCH_CITYKEY_COOKIE", encodeURIComponent(v), 1000*60*60*24*30, ".jobui.com");
		loadCity();
	}
}
