function loadCity(){
	var r = cookieGet("SEARCH_CITYKEY_COOKIE",true);   //从cookie取变量
	if(r && r != "") {
		if($("jobuiNowCity")) {
			$("jobuiNowCity").innerHTML=r;
		}
		if($("jobuiNowCity1")) {
			$("jobuiNowCity1").innerHTML=r;
		}
		if($("jobuiNowCity2")) {
			$("jobuiNowCity2").innerHTML=r;
		}
		if($("jobuiNowCity3")) {
			$("jobuiNowCity3").innerHTML=r;
		}
		if($("jobuiNowCity4")) {
			$("jobuiNowCity4").innerHTML=r;
		}
		if($("cityKw") && location.href.indexOf("so.jobui.com/search.")==-1) {
			$("cityKw").value = r;
		}
		if($("nowArea")) {//分类搜索
			$("nowArea").innerHTML=r;
		}
		/*
		if($("indexHotInfo")) {
			ajaxPostDateToPage("/action/getHotForCity.php","jobuiNowCity="+r,"indexHotInfo");
		}
		*/
		
	} else {
		r = ajaxGetDate("/action/getCityFromIPAjax.php", saveNowCityFromAjax, "TEXT");
	}
}
function goSearchUrl(jobkw, cityKw, industryKw) {
	document.location.href="http://so.jobui.com/search.php?jobKw="+jobkw+"&cityKw="+cityKw+"&jobSort=&indSort=0"+industryKw+"";
}
// 城市切换所需的
function hideDiv() {
	if ($("indexSearchCity")) { $("indexSearchCity").style.display = "none"; }
	if ($("indexSearchCity2")) { $("indexSearchCity2").style.display = "none"; }
	if ($("indexMoreCity")) { $("indexMoreCity").style.display = "none"; }
	if ($("choice_select")) { $("choice_select").style.display = "none"; }
	if ($("choice_select_2")) { $("choice_select_2").style.display = "none"; }
}
function cancelBubble(oEvent) {
	if (document.all) { // ie
		oEvent.cancelBubble = true;
	} else {	// ff
		oEvent.stopPropagation();
	}
}
function check(){
	if(!$("cityKw").value && ($("jobKw").getAttribute('isinputed')=='false' || !$("jobKw").value)) {
		alert("您还没有输入关键字");
		$("jobKw").focus();
		return false;
	}
	if ($("jobKw").getAttribute('isinputed')=='false') {
		$("jobKw").value="";
	}
	return true;
}

//右上角退出系统
function topRightLogout(){
	top.ajaxPostDate("/action/leftbar_exit.php","formAction=personalStatusPopupExit",
	function(){
		top.location.reload();
	});
}
//打开信息页
function showPage(){
	if(!top.jobuiLeftbarStatus) {
		top.showJobuiLeftbar();
	}
	setTimeout(function(){
		if(frames[0].openedMenuID != 6) {
			frames[0].chageMenu('6');
		}
		frames[0].chageSelection(frames[0].getEle('message_inbox').childNodes[0]);
		frames[0].changeIframeChannel('message_inbox');
	},400);
	
}


//在右上角显示"登录&注册"链接
function addTopLogin() {
	if($("divTopRight")) {
		var loginStr = '';
		if(!top.isPersonalLogin) {
			loginStr = '<a href="javascript:void(0);" onclick="javascript:if(!top.jobuiLeftbarStatus)top.showJobuiLeftbar();">登录</a>&nbsp;&nbsp;|&nbsp;&nbsp;';
		} else {
			loginStr = '<span id="topUserNickName"></span>&nbsp;&nbsp;|&nbsp;&nbsp;<a href="javascript:void(0);" onclick="topRightLogout()">退出</a>&nbsp;&nbsp;|&nbsp;&nbsp;';
			
		}
		loginStr = loginStr + $("divTopRight").innerHTML ;
		$("divTopRight").innerHTML = loginStr;
	}
}
