//获取用户昵称
function ajaxGetTopUserNickName(){
	top.ajaxPostDate("/action/personal/personal.php","formAction=ajaxGetTopUserNickName",function(o){
		if($("topUserNickName")) {
			$("topUserNickName").innerHTML = "<font  style='font-weight: bold;'>" + o.returnMessageText + "</font>&nbsp;&nbsp;<span id='checkMessage'></span><a href='#' onclick='if(!top.jobuiLeftbarStatus)top.showJobuiLeftbar();'>我的职友集</a>";
		}	
	},"TEXT");

	//若登陆检查一次是否有新信息，有则显示
	setTimeout(function(){
		top.ajaxPostDate("/action/personal/index.php","formAction=message_checkMessage",function(o){
			var result=o.returnMessageText;
			if(result && result !="0" && result.indexOf("用户") ==-1) {
				if($("checkMessage")) {
					$("checkMessage").innerHTML = "<a href='javascript:void(0);' style='text-decoration:none' onclick='showPage();' title = '你有"+result+"条新消息,请注意查收'><img src='http://htm.jobui.com/images_so/new_msg2.gif' border='0' alt = '你有"+result+"条新消息,请注意查收'></a>&nbsp;&nbsp;";
				}
			}
	},"TEXT")},500);
}

